export const initalState = {
  mainPosts: [{
    id: 1,
    User: {
      id: 1,
      nickname: '제로초',
    },
    content: '첫 번째 게시글 #해시테그 #익스프래스',
    Images: [{
      src: 'https://t1.daumcdn.net/daumtop_chanel/op/20201207054226144.png',
    }, {
      src: 'https://www.google.co.kr/logos/doodles/2020/december-holidays-days-2-30-6753651837108830.3-law.gif',
    }, {
      src: 'http://img.mimacstudy.com/FRONT/main/renew2017/dsm_logo.gif',
    }],
    Comments: [{
      User: {
        nickname: 'nero',
      },
      content: '우와 개정판~',
    }, {
      User: {
        nickname: 'hero',
      },
      content: '얼른 사고 싶어요~',
    }],
  }],
  imagePaths: [],
  postAdded: false,
};

const ADD_POST = 'ADD_POST';
export const addPost = {
  type: ADD_POST,
};

const dummyPost = {
  id: 2,
  content: '더미데이터',
  User: {
    id: 1,
    nickname: '제로초',
  },
};

const reducer = (state = initalState, action) => {
  switch (action.data) {
    case ADD_POST:
    default:
      return state;
  }
};
export default reducer;
