export const initalState = {
  isLoggedIn: false,
  user: null,
  signUpData: {},
  loginData: {},
};

export const loginAction = (data) => ({
  type: 'LOG_IN',
  data,
});

export const logoutAction = () => ({
  type: 'LOG_OUT',
});

const reducer = (state = initalState, action) => {
  switch (action.data) {
    case 'LOG_IN':
      return {
        ...state,
        isLoggedIn: true,
        user: action.data,
      };
    case 'LOG_OUT':
      return {
        ...state,
        isLoggedIn: false,
      };
    default:
      return state;
  }
};
console.log(isLoggedIn);
export default reducer;
