import { HYDRATE } from 'next-redux-wrapper';

const initialSteate = {
  user: {
    isLoggedIn: false,
    user: null,
    signUpdata: {},
    loginData: {},
  },
  post: {
    mainPosts: [],
  },
};

export const loginAction = (data) => {
  return {
    type: 'LOG_IN',
    data,
  };
};

export const logoutAction = () => {
  return {
    type: 'LOG_OUT',
  };
};

const CHANGE_NICKNAME = {
  type: 'CHANGE_NICKNAME',
  data: 'boogicho',
};

const rootReducer = (state = initialSteate, action) => {
  switch (action.type) {
    case HYDRATE:
      console.log('HYDRATE', action);
      return {
        ...state, ...action.payload,
      };
    case 'LOG_IN':
      return {
        ...state,
        user: {
          ...state.user,
          isLoggedIn: true,
          user: action.data,
        },
      };
    case 'LOG_OUT':
      return {
        ...state,
        user: {
          ...state.user,
          isLoggedIn: false,
        },
      };
    default:
      break;
  }
  return true;
};

export default rootReducer;
